import java.io.IOException;import io.grpc.Server;import io.grpc.ServerBuilder;import user.UserService;public class GrpcServer {	private static final int GRPC_SERVER_PORT = 9090;	public static void main(String[] args) throws IOException, InterruptedException {		System.out.println("Starting GRPC Server");		Server server = ServerBuilder.forPort(GRPC_SERVER_PORT).addService(new UserService()).build();		server.start();		System.out.println("server started at " + server.getPort());		server.awaitTermination();	}}